package fundamentals;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// store integral number
		int a = 10;
		short s = 15;
		byte b = 11;
		long l = 117;
		
		// store decimal number
		double d = 10.5; // 8 bytes and high precision to store
		float f = 10.5f; // 4 bytes and low precision to store
		
		// store character
		char ch = 'a';
		
		boolean b1 = true;
		
		
		//--------------------------------------------------------------
		
		
		int val;
//		System.out.println(val);  gives error
		val = 10; 
		// in java we cannot use a variable before initialization 
	}

}
